mapper.select:
SELECT 
    feedback_date AS date,
    completion_rate AS completionRate,
    rating
FROM user_feedback
WHERE user_id = #{userId}
  AND feedback_date >= #{startDate}
  AND feedback_date <= #{endDate}
ORDER BY feedback_date ASC

