mapper.select:
        SELECT id, user_id AS userId, plan_json AS planJson, date, created_at AS createdAt
        FROM user_plan
WHERE user_id = #{userId}
  AND date >= #{startDate}
  AND date <= #{endDate}
ORDER BY date DESC

SELECT id, user_id AS userId, plan_json AS planJson, date, created_at AS createdAt
FROM user_plan
WHERE user_id = #{userId}
  AND date = #{date}
LIMIT 1
